import React, { useState, useEffect, useRef } from 'react';
import './App.css';

function App() {
  const [servers, setServers] = useState([]);
  const [selectedServer, setSelectedServer] = useState(null);
  const [messages, setMessages] = useState([]);
  const [command, setCommand] = useState('');
  const [commandOutput, setCommandOutput] = useState([]);
  const [systemMetrics, setSystemMetrics] = useState({
    cpuUsage: 0,
    ramUsage: 0,
    diskUsage: 0,
    networkActivity: { bytes_sent: 0, bytes_recv: 0 }
  });
  const [serverMetrics, setServerMetrics] = useState({});
  
  const websocketRef = useRef(null);
  const messagesEndRef = useRef(null);

  // Scroll to bottom of messages
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages, commandOutput]);

  useEffect(() => {
    // Connect to WebSocket
    const websocket = new WebSocket("ws://localhost:8000/ws/1");
    websocketRef.current = websocket;

    websocket.onopen = () => {
      console.log("Connected to WebSocket");
      setMessages(prev => [...prev, "Connected to MCP Switchboard"]);
    };

    websocket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      console.log("Message from server:", data);
      
      if (data.type === "server_list") {
        setServers(data.servers);
        if (data.servers.length > 0 && !selectedServer) {
          setSelectedServer(data.servers[0].id);
        }
      } 
      else if (data.type === "server_status_update") {
        setServers(prev => prev.map(server => 
          server.id === data.server_id 
            ? { ...server, status: data.status } 
            : server
        ));
        setMessages(prev => [...prev, data.message]);
      } 
      else if (data.type === "command_result") {
        setCommandOutput(prev => [...prev, {
          server: data.server_id,
          success: data.success,
          output: data.output
        }]);
        setMessages(prev => [...prev, data.message]);
      } 
      else if (data.type === "server_metrics") {
        setServerMetrics(prev => ({
          ...prev,
          [data.server_id]: data.metrics
        }));
      }
      else {
        setMessages(prev => [...prev, JSON.stringify(data)]);
      }
    };

    websocket.onclose = () => {
      console.log("Disconnected from WebSocket");
      setMessages(prev => [...prev, "Disconnected from MCP Switchboard"]);
    };

    return () => {
      websocket.close();
    };
  }, []);

  // Fetch system metrics
  useEffect(() => {
    const fetchSystemMetrics = async () => {
      try {
        const response = await fetch("http://localhost:8000/metrics", {
          headers: {
            'Authorization': 'Bearer fake_token'
          }
        });
        if (response.ok) {
          const data = await response.json();
          setSystemMetrics({
            cpuUsage: data.cpu_usage,
            ramUsage: data.ram_usage,
            diskUsage: data.disk_usage,
            networkActivity: data.network_activity
          });
        } else {
          console.error("Failed to fetch system metrics");
        }
      } catch (error) {
        console.error("Error fetching system metrics:", error);
      }
    };

    fetchSystemMetrics();
    const intervalId = setInterval(fetchSystemMetrics, 5000);

    return () => clearInterval(intervalId);
  }, []);

  // Connect to a server
  const handleConnectServer = (serverId) => {
    if (websocketRef.current && websocketRef.current.readyState === WebSocket.OPEN) {
      websocketRef.current.send(JSON.stringify({
        type: "connect_server",
        server_id: serverId
      }));
    }
  };

  // Disconnect from a server
  const handleDisconnectServer = (serverId) => {
    if (websocketRef.current && websocketRef.current.readyState === WebSocket.OPEN) {
      websocketRef.current.send(JSON.stringify({
        type: "disconnect_server",
        server_id: serverId
      }));
    }
  };

  // Execute a command
  const handleSubmit = async (event) => {
    event.preventDefault();
    
    if (!selectedServer) {
      setMessages(prev => [...prev, "No server selected"]);
      return;
    }
    
    if (!command.trim()) {
      setMessages(prev => [...prev, "Command cannot be empty"]);
      return;
    }
    
    // Send command via WebSocket
    if (websocketRef.current && websocketRef.current.readyState === WebSocket.OPEN) {
      websocketRef.current.send(JSON.stringify({
        type: "execute_command",
        server_id: selectedServer,
        command: command
      }));
      setCommand("");
    } else {
      // Fallback to REST API if WebSocket is not available
      try {
        const response = await fetch(`http://localhost:8000/execute/${selectedServer}`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/x-www-form-urlencoded',
            'Authorization': 'Bearer fake_token'
          },
          body: `command=${command}`
        });

        if (response.ok) {
          const data = await response.json();
          console.log("Command executed successfully:", data);
          setMessages(prev => [...prev, data.message]);
          setCommandOutput(prev => [...prev, {
            server: selectedServer,
            success: true,
            output: data.output || data.message
          }]);
          setCommand("");
        } else {
          console.error("Command execution failed:", response.status);
          setMessages(prev => [...prev, `Command execution failed: ${response.status}`]);
        }
      } catch (error) {
        console.error("Error executing command:", error);
        setMessages(prev => [...prev, `Error executing command: ${error.message}`]);
      }
    }
  };

  // Format metrics for display
  const formatBytes = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>MCP Switchboard</h1>
      </header>
      
      {/* Server Management Section */}
      <section className="section">
        <h2>Server Management</h2>
        <div className="server-grid">
          {servers.map(server => (
            <div 
              key={server.id} 
              className={`server-block ${selectedServer === server.id ? 'selected' : ''}`}
              onClick={() => setSelectedServer(server.id)}
            >
              <h3>{server.name}</h3>
              <p>Host: {server.host}</p>
              <p>Port: {server.port}</p>
              <p>Type: {server.type}</p>
              <p className={`status ${server.status ? 'active' : 'inactive'}`}>
                Status: {server.status ? 'Connected' : 'Disconnected'}
              </p>
              <div className="server-actions">
                {!server.status ? (
                  <button onClick={() => handleConnectServer(server.id)}>Connect</button>
                ) : (
                  <button onClick={() => handleDisconnectServer(server.id)}>Disconnect</button>
                )}
              </div>
            </div>
          ))}
        </div>
      </section>
      
      {/* Task Execution Section */}
      <section className="section">
        <h2>Task Execution</h2>
        <div className="task-execution">
          <form onSubmit={handleSubmit}>
            <div className="command-input">
              <select 
                value={selectedServer || ''} 
                onChange={(e) => setSelectedServer(Number(e.target.value))}
              >
                <option value="">Select Server</option>
                {servers.map(server => (
                  <option key={server.id} value={server.id} disabled={!server.status}>
                    {server.name} {!server.status ? '(Disconnected)' : ''}
                  </option>
                ))}
              </select>
              <input
                type="text"
                value={command}
                onChange={(e) => setCommand(e.target.value)}
                placeholder="Enter command..."
                disabled={!selectedServer || !servers.find(s => s.id === selectedServer)?.status}
              />
              <button 
                type="submit" 
                disabled={!selectedServer || !servers.find(s => s.id === selectedServer)?.status}
              >
                Execute
              </button>
            </div>
          </form>
          
          <div className="console-output">
            <h3>Console Output</h3>
            <div className="output-container">
              {commandOutput.map((output, index) => (
                <div key={index} className={`output-line ${output.success ? 'success' : 'error'}`}>
                  <span className="server-tag">Server {output.server}:</span> {output.output}
                </div>
              ))}
              <div ref={messagesEndRef} />
            </div>
          </div>
        </div>
      </section>
      
      {/* Metrics Section */}
      <section className="section">
        <h2>Metrics</h2>
        <div className="metrics-container">
          <div className="metrics-block">
            <h3>System Metrics</h3>
            <div className="metric">
              <span>CPU Usage:</span>
              <div className="progress-bar">
                <div className="progress" style={{ width: `${systemMetrics.cpuUsage}%` }}></div>
              </div>
              <span>{systemMetrics.cpuUsage.toFixed(1)}%</span>
            </div>
            <div className="metric">
              <span>RAM Usage:</span>
              <div className="progress-bar">
                <div className="progress" style={{ width: `${systemMetrics.ramUsage}%` }}></div>
              </div>
              <span>{systemMetrics.ramUsage.toFixed(1)}%</span>
            </div>
            <div className="metric">
              <span>Disk Usage:</span>
              <div className="progress-bar">
                <div className="progress" style={{ width: `${systemMetrics.diskUsage}%` }}></div>
              </div>
              <span>{systemMetrics.diskUsage.toFixed(1)}%</span>
            </div>
            <div className="metric">
              <span>Network:</span>
              <span>
                ↑ {formatBytes(systemMetrics.networkActivity.bytes_sent)} | 
                ↓ {formatBytes(systemMetrics.networkActivity.bytes_recv)}
              </span>
            </div>
          </div>
          
          {selectedServer && serverMetrics[selectedServer] && (
            <div className="metrics-block">
              <h3>Server Metrics</h3>
              <div className="metric">
                <span>CPU Usage:</span>
                <div className="progress-bar">
                  <div className="progress" style={{ width: `${serverMetrics[selectedServer].cpu_usage}%` }}></div>
                </div>
                <span>{serverMetrics[selectedServer].cpu_usage.toFixed(1)}%</span>
              </div>
              <div className="metric">
                <span>Memory:</span>
                <div className="progress-bar">
                  <div className="progress" style={{ width: `${(serverMetrics[selectedServer].memory_usage / 1024).toFixed(1)}%` }}></div>
                </div>
                <span>{formatBytes(serverMetrics[selectedServer].memory_usage * 1024 * 1024)}</span>
              </div>
              <div className="metric">
                <span>Disk:</span>
                <div className="progress-bar">
                  <div className="progress" style={{ width: `${(serverMetrics[selectedServer].disk_usage / 1024).toFixed(1)}%` }}></div>
                </div>
                <span>{formatBytes(serverMetrics[selectedServer].disk_usage * 1024 * 1024)}</span>
              </div>
              <div className="metric">
                <span>Network:</span>
                <span>
                  ↑ {formatBytes(serverMetrics[selectedServer].network_in * 1024)} | 
                  ↓ {formatBytes(serverMetrics[selectedServer].network_out * 1024)}
                </span>
              </div>
            </div>
          )}
        </div>
      </section>
      
      {/* System Messages */}
      <section className="section">
        <h2>System Messages</h2>
        <div className="messages-container">
          {messages.map((message, index) => (
            <div key={index} className="message">{message}</div>
          ))}
          <div ref={messagesEndRef} />
        </div>
      </section>
    </div>
  );
}

export default App;
