import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import AddServerForm from './AddServerForm';
import EditServerForm from './EditServerForm';
import DeleteServerConfirmation from './DeleteServerConfirmation';

function App() {
  const [servers, setServers] = useState([]);
  const [selectedServer, setSelectedServer] = useState(null);
  const [messages, setMessages] = useState([]);
  const [command, setCommand] = useState('');
  const [commandOutput, setCommandOutput] = useState([]);
  const [systemMetrics, setSystemMetrics] = useState({
    cpuUsage: 0,
    ramUsage: 0,
    diskUsage: 0,
    networkActivity: { bytes_sent: 0, bytes_recv: 0 }
  });
  const [activeMenuItem, setActiveMenuItem] = useState('Dashboard');
  const [editingServer, setEditingServer] = useState(null);
  const [deletingServer, setDeletingServer] = useState(null);
  const [autoScroll, setAutoScroll] = useState(true);
  
  const websocketRef = useRef(null);
  const messagesEndRef = useRef(null);

  // Scroll to bottom of messages
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    if (autoScroll) {
      scrollToBottom();
    }
  }, [messages, commandOutput, autoScroll]);

  useEffect(() => {
    // Connect to WebSocket
    const websocket = new WebSocket("ws://localhost:8000/ws/1");
    websocketRef.current = websocket;

    websocket.onopen = () => {
      console.log("Connected to WebSocket");
      setMessages(prev => [...prev, "Connected to MCP Switchboard"]);
    };

    websocket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      console.log("Message from server:", data);
      
      if (data.type === "server_list") {
        setServers(data.servers);
        if (data.servers.length > 0 && !selectedServer) {
          setSelectedServer(data.servers[0].id);
        }
      } 
      else if (data.type === "server_status_update") {
        setServers(prev => prev.map(server => 
          server.id === data.server_id 
            ? { ...server, status: data.status } 
            : server
        ));
        setMessages(prev => [...prev, data.message]);
      } 
      else if (data.type === "command_result") {
        setCommandOutput(prev => [...prev, {
          server: data.server_id,
          success: data.success,
          output: data.output
        }]);
        setMessages(prev => [...prev, data.message]);
      } 
      else {
        setMessages(prev => [...prev, JSON.stringify(data)]);
      }
    };

    websocket.onclose = () => {
      console.log("Disconnected from WebSocket");
      setMessages(prev => [...prev, "Disconnected from MCP Switchboard"]);
    };

    return () => {
      websocket.close();
    };
  }, [selectedServer]);

  const fetchSystemMetrics = async () => {
    try {
      const response = await fetch("http://localhost:8000/metrics", {
        headers: {
          'Content-Type': 'application/json'
        }
      });
      if (response.ok) {
        const data = await response.json();
        setSystemMetrics({
          cpuUsage: data.cpu_usage,
          ramUsage: data.ram_usage,
          diskUsage: data.disk_usage,
          networkActivity: data.network_activity
        });
      } else {
        console.error("Failed to fetch system metrics");
      }
    } catch (error) {
      console.error("Error fetching system metrics:", error);
    }
  };

  // Fetch system metrics
  useEffect(() => {
    fetchSystemMetrics();
    const intervalId = setInterval(fetchSystemMetrics, 5000);

    return () => clearInterval(intervalId);
  }, []);

  // Connect to a server
  const handleConnectServer = (serverId, e) => {
    e.stopPropagation();
    if (websocketRef.current && websocketRef.current.readyState === WebSocket.OPEN) {
      websocketRef.current.send(JSON.stringify({
        type: "connect_server",
        server_id: serverId
      }));
    }
  };

  // Disconnect from a server
  const handleDisconnectServer = (serverId, e) => {
    e.stopPropagation();
    if (websocketRef.current && websocketRef.current.readyState === WebSocket.OPEN) {
      websocketRef.current.send(JSON.stringify({
        type: "disconnect_server",
        server_id: serverId
      }));
    }
  };

  // Execute a command
  const handleSubmit = async (event) => {
    event.preventDefault();
    
    if (!selectedServer) {
      setMessages(prev => [...prev, "No server selected"]);
      return;
    }
    
    if (!command.trim()) {
      setMessages(prev => [...prev, "Command cannot be empty"]);
      return;
    }
    
    // Send command via WebSocket
    if (websocketRef.current && websocketRef.current.readyState === WebSocket.OPEN) {
      websocketRef.current.send(JSON.stringify({
        type: "execute_command",
        server_id: selectedServer,
        command: command
      }));
      setCommand("");
    } else {
      // Fallback to REST API if WebSocket is not available
      try {
        const response = await fetch(`http://localhost:8000/execute/${selectedServer}`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/x-www-form-urlencoded'
          },
          body: `command=${command}`
        });

        if (response.ok) {
          const data = await response.json();
          console.log("Command executed successfully:", data);
          setMessages(prev => [...prev, data.message]);
          setCommandOutput(prev => [...prev, {
            server: selectedServer,
            success: true,
            output: data.output || data.message
          }]);
          setCommand("");
        } else {
          console.error("Command execution failed:", response.status);
          setMessages(prev => [...prev, `Command execution failed: ${response.status}`]);
        }
      } catch (error) {
        console.error("Error executing command:", error);
        setMessages(prev => [...prev, `Error executing command: ${error.message}`]);
      }
    }
  };

  // Format metrics for display
  const formatBytes = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  // Handle editing a server
  const handleEditServer = (server, e) => {
    e.stopPropagation();
    setEditingServer(server);
  };

  // Handle canceling server edit
  const handleCancelEdit = () => {
    setEditingServer(null);
  };

  // Handle server update
  const handleServerUpdate = () => {
    fetchSystemMetrics();
    setEditingServer(null);
  };

  // Handle deleting a server
  const handleDeleteServer = (server, e) => {
    e.stopPropagation();
    setDeletingServer(server);
  };

  // Handle canceling server deletion
  const handleCancelDelete = () => {
    setDeletingServer(null);
  };

  // Handle server deletion
  const handleServerDelete = () => {
    fetchSystemMetrics();
    setDeletingServer(null);
  };

  return (
    <div className="App">
      {/* Fixed Buttons */}
      <div>
        {/* Auto-scroll Toggle Button */}
        <div 
          style={{
            position: 'fixed',
            bottom: '20px',
            right: '20px',
            zIndex: 1000,
            backgroundColor: autoScroll ? 'var(--primary-color)' : '#95a5a6',
            color: 'white',
            padding: '10px 15px',
            borderRadius: '5px',
            cursor: 'pointer',
            boxShadow: '0 2px 5px rgba(0,0,0,0.2)',
            display: 'flex',
            alignItems: 'center',
            gap: '8px'
          }}
          onClick={() => setAutoScroll(!autoScroll)}
        >
          <span style={{ fontSize: '16px' }}>{autoScroll ? 'ðŸ“Œ Auto-scroll: ON' : 'ðŸ“Œ Auto-scroll: OFF'}</span>
        </div>
        
        {/* Scroll to Top Button */}
        <div 
          style={{
            position: 'fixed',
            bottom: '20px',
            left: '20px',
            zIndex: 1000,
            backgroundColor: 'var(--secondary-color)',
            color: 'white',
            padding: '10px 15px',
            borderRadius: '5px',
            cursor: 'pointer',
            boxShadow: '0 2px 5px rgba(0,0,0,0.2)',
            display: 'flex',
            alignItems: 'center',
            gap: '8px'
          }}
          onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
        >
          <span style={{ fontSize: '16px' }}>â¬†ï¸ Scroll to Top</span>
        </div>
        
        {/* Quick Add Server Button */}
        <div 
          style={{
            position: 'fixed',
            top: '80px',
            right: '20px',
            zIndex: 1000,
            backgroundColor: 'var(--primary-color)',
            color: 'white',
            padding: '10px 15px',
            borderRadius: '5px',
            cursor: 'pointer',
            boxShadow: '0 2px 5px rgba(0,0,0,0.2)',
            display: 'flex',
            alignItems: 'center',
            gap: '8px'
          }}
          onClick={() => document.querySelector('.add-server-button')?.click()}
        >
          <span style={{ fontSize: '16px' }}>âž• Add New Server</span>
        </div>
      </div>
      {/* Header */}
      <header className="App-header">
        <div className="logo">
          <span className="logo-icon">ðŸŽ›ï¸</span>
          <h1>MCP SwitchBoard</h1>
        </div>
        <div className="user-menu">
          <button>Notifications</button>
          <button>Settings</button>
          <button>Admin</button>
        </div>
      </header>
      
      {/* Sidebar */}
      <aside className="sidebar">
        <div className="sidebar-section">
          <h3>Main Menu</h3>
          <ul className="sidebar-menu">
            <li className={activeMenuItem === 'Dashboard' ? 'active' : ''} onClick={() => setActiveMenuItem('Dashboard')}>Dashboard</li>
            <li className={activeMenuItem === 'Servers' ? 'active' : ''} onClick={() => setActiveMenuItem('Servers')}>Servers</li>
            <li className={activeMenuItem === 'Tasks' ? 'active' : ''} onClick={() => setActiveMenuItem('Tasks')}>Tasks</li>
            <li className={activeMenuItem === 'Monitoring' ? 'active' : ''} onClick={() => setActiveMenuItem('Monitoring')}>Monitoring</li>
            <li className={activeMenuItem === 'Logs' ? 'active' : ''} onClick={() => setActiveMenuItem('Logs')}>Logs</li>
          </ul>
        </div>
        <div className="sidebar-section">
          <h3>MCP Servers</h3>
          <ul className="sidebar-menu">
            {servers.map(server => (
              <li key={server.id} onClick={() => setSelectedServer(server.id)}>
                {server.name}
              </li>
            ))}
          </ul>
        </div>
        <div className="sidebar-section">
          <h3>Settings</h3>
          <ul className="sidebar-menu">
            <li>User Profile</li>
            <li>Preferences</li>
            <li>API Keys</li>
            <li>Help</li>
          </ul>
        </div>
      </aside>
      
      {/* Main Content */}
      <main className="main-content">
        <h2 className="page-title">Dashboard</h2>
        
        {/* Server Management Section */}
        <section className="section">
          <h2>Server Management</h2>
          <AddServerForm onAdd={fetchSystemMetrics} />
          {editingServer ? (
            <EditServerForm 
              server={editingServer} 
              onUpdate={handleServerUpdate} 
              onCancel={handleCancelEdit} 
            />
          ) : null}
          {deletingServer ? (
            <DeleteServerConfirmation 
              server={deletingServer} 
              onConfirm={handleServerDelete} 
              onCancel={handleCancelDelete} 
            />
          ) : null}
          <div className="server-grid">
            {servers.map(server => (
              <div
                key={server.id}
                className={`server-block ${selectedServer === server.id ? 'selected' : ''}`}
                onClick={() => setSelectedServer(server.id)}
              >
                <h3>{server.name}</h3>
                <div className="server-info">
                  <p>Status: <span>{server.status ? 'Active' : 'Inactive'}</span></p>
                  <p>Host: <span>{server.host}</span></p>
                  <p>Port: <span>{server.port}</span></p>
                  <p>Type: <span>{server.type}</span></p>
                </div>
                <div className="server-actions">
                  {!server.status ? (
                    <button onClick={(e) => handleConnectServer(server.id, e)}>Connect</button>
                  ) : (
                    <button onClick={(e) => handleDisconnectServer(server.id, e)}>Disconnect</button>
                  )}
                  <button onClick={(e) => handleEditServer(server, e)}>Edit</button>
                  <button onClick={(e) => handleDeleteServer(server, e)} style={{ backgroundColor: 'var(--danger-color)' }}>Delete</button>
                  <button>Details</button>
                </div>
              </div>
            ))}
          </div>
        </section>
        
        {/* Metrics Section */}
        <h2 className="page-title">Server Metrics</h2>
        <section className="section">
          <div className="metrics-container">
            <div className="metrics-block">
              <h3>CPU Usage</h3>
              <div className="metric-value">{systemMetrics.cpuUsage.toFixed(1)}%</div>
              <div className="progress-container">
                <div className="progress-bar" style={{ width: `${systemMetrics.cpuUsage}%`, backgroundColor: 'var(--primary-color)' }}></div>
              </div>
            </div>
            
            <div className="metrics-block">
              <h3>RAM Usage</h3>
              <div className="metric-value">{systemMetrics.ramUsage.toFixed(1)}%</div>
              <div className="progress-container">
                <div className="progress-bar" style={{ width: `${systemMetrics.ramUsage}%`, backgroundColor: 'var(--warning-color)' }}></div>
              </div>
            </div>
            
            <div className="metrics-block">
              <h3>Disk Usage</h3>
              <div className="metric-value">{systemMetrics.diskUsage.toFixed(1)}%</div>
              <div className="progress-container">
                <div className="progress-bar" style={{ width: `${systemMetrics.diskUsage}%`, backgroundColor: 'var(--secondary-color)' }}></div>
              </div>
            </div>
            
            <div className="metrics-block">
              <h3>Network Activity</h3>
              <div className="metric-value">
                â†‘ {formatBytes(systemMetrics.networkActivity.bytes_sent)} | 
                â†“ {formatBytes(systemMetrics.networkActivity.bytes_recv)}
              </div>
              <div className="progress-container">
                <div className="progress-bar" style={{ width: '60%', backgroundColor: 'var(--danger-color)' }}></div>
              </div>
            </div>
          </div>
        </section>
        
        {/* Task Execution Section */}
        <h2 className="page-title">Task Execution</h2>
        <section className="section">
          <div className="task-execution">
            <div className="task-card">
              <h3>Execute Command</h3>
              <form onSubmit={handleSubmit}>
                <div className="form-group">
                  <label htmlFor="server-select">Select Server</label>
                  <select 
                    id="server-select"
                    className="form-control"
                    value={selectedServer || ''} 
                    onChange={(e) => setSelectedServer(Number(e.target.value))}
                  >
                    <option value="">Select Server</option>
                    {servers.map(server => (
                      <option key={server.id} value={server.id} disabled={!server.status}>
                        {server.name} {!server.status ? '(Disconnected)' : ''}
                      </option>
                    ))}
                  </select>
                </div>
                <div className="form-group">
                  <label htmlFor="command-input">Command</label>
                  <input
                    id="command-input"
                    type="text"
                    className="form-control"
                    value={command}
                    onChange={(e) => setCommand(e.target.value)}
                    placeholder="Enter command..."
                    disabled={!selectedServer || !servers.find(s => s.id === selectedServer)?.status}
                  />
                </div>
                <button 
                  type="submit" 
                  className="btn btn-primary"
                  disabled={!selectedServer || !servers.find(s => s.id === selectedServer)?.status}
                >
                  Execute
                </button>
              </form>
            </div>
            
            <div className="task-card">
              <h3>Console Output</h3>
              <div className="console-output">
                {commandOutput.map((output, index) => (
                  <div key={index} className={`console-line ${output.success ? 'success' : 'error'}`}>
                    <span className="server-tag">Server {output.server}:</span> {output.output}
                  </div>
                ))}
                <div ref={messagesEndRef} />
              </div>
            </div>
          </div>
        </section>
        
        {/* System Messages */}
        <h2 className="page-title">System Messages</h2>
        <section className="section">
          <div className="messages-container">
            {messages.map((message, index) => (
              <div key={index} className="message">{message}</div>
            ))}
            <div ref={messagesEndRef} />
          </div>
        </section>
      </main>
    </div>
  );
}

export default App;
